mapper.select:
update hpzc_cgd set
is_delete='1' where
h_id=#{0}

		select
		
		from hpzc_cgd
		where is_delete='0'
		
			and h_play_code like CONCAT('%',#{zdr},'%')
		
		
			and h_pur_name like CONCAT('%',#{cgr},'%')
		
		
			and h_gysbm like CONCAT('%',#{gys},'%')
		
		
			and h_id = #{hid}
		
		
			and h_code = #{hCode}
		
		
	   and DATE_FORMAT(h_cjrq, '%Y-%m-%d')>=  DATE_FORMAT(#{zdbegin}, '%Y-%m-%d')   
		
		
	  and DATE_FORMAT(h_cjrq, '%Y-%m-%d') <= DATE_FORMAT(#{zdend}, '%Y-%m-%d')    
		
		
		
  	      and h_date >= #{cgbegin}  
		
		
		
    	  and h_date <= #{cgend} 
		
		
  	      and h_pur_money >= #{minje}  
		
		
  	      and h_pur_money >= #{maxje}

update.foreach:
update hpzc_cgd set is_delete='1'
where h_id
=${item}
